package packet

import (
	"fmt"
	"net"
)

func WriteS2CStatus(connection net.Conn) {
	bufferedPacket := BufferedPacket{}
	status := `{
		"description": { "text": "Powered by man.go!" },
		"version": { "name": "1.16.5", "protocol": 754 },
		"players": { "max": 5, "online": -69 },
		"favicon": "data:image/png;base64,"
	}`
	bufferedPacket.WriteString(status)

	fmt.Printf(`
Sending Status Packet
`)
	connection.Write(bufferedPacket.GetBufferWithHeader(0))
}
